import React, { useState } from 'react';

const WelcomeButton = () => {
  const [welcomeMessage, setWelcomeMessage] = useState('');

  // Function that takes "welcome" as an argument
  const displayWelcomeMessage = (messageType) => {
    const messages = {
      welcome: 'Welcome to our React Event Examples Application!',
      greeting: 'Greetings! Thanks for visiting our app!',
      hello: 'Hello there! Hope you enjoy learning React events!'
    };
    
    setWelcomeMessage(messages[messageType] || 'Thanks for clicking!');
  };

  const clearMessage = () => {
    setWelcomeMessage('');
  };

  return (
    <div className="component-section">
      <h2>Welcome Button with Arguments</h2>
      
      <div className="button-group">
        <button 
          className="btn btn-info" 
          onClick={() => displayWelcomeMessage('welcome')}
        >
          Say Welcome
        </button>
        <button 
          className="btn btn-info" 
          onClick={() => displayWelcomeMessage('greeting')}
        >
          Say Greeting
        </button>
        <button 
          className="btn btn-info" 
          onClick={() => displayWelcomeMessage('hello')}
        >
          Say Hello
        </button>
        <button 
          className="btn btn-warning" 
          onClick={clearMessage}
        >
          Clear Message
        </button>
      </div>
      
      <div className="message-display">
        {welcomeMessage}
      </div>
    </div>
  );
};

export default WelcomeButton;
