import React, { useState } from 'react';

const Counter = () => {
  const [count, setCount] = useState(0);
  const [message, setMessage] = useState('');

  // Multiple methods for increment
  const sayHello = () => {
    setMessage('Hello! Welcome to React Event Handling!');
  };

  const incrementValue = () => {
    setCount(prevCount => prevCount + 1);
  };

  const displayStaticMessage = () => {
    setTimeout(() => {
      setMessage(prevMsg => prevMsg + ' - Counter has been incremented!');
    }, 100);
  };

  // Increment button that invokes multiple methods
  const handleIncrement = () => {
    sayHello();
    incrementValue();
    displayStaticMessage();
  };

  const handleDecrement = () => {
    setCount(prevCount => prevCount - 1);
    setMessage('Counter decremented!');
  };

  const resetCounter = () => {
    setCount(0);
    setMessage('Counter has been reset!');
  };

  return (
    <div className="component-section">
      <h2>Counter with Multiple Event Handlers</h2>
      
      <div className="counter-display">
        Current Count: {count}
      </div>
      
      <div className="button-group">
        <button className="btn" onClick={handleIncrement}>
          Increment
        </button>
        <button className="btn btn-danger" onClick={handleDecrement}>
          Decrement
        </button>
        <button className="btn btn-warning" onClick={resetCounter}>
          Reset
        </button>
      </div>
      
      <div className="message-display">
        {message}
      </div>
    </div>
  );
};

export default Counter;
