import React from 'react';
import './App.css';
import Counter from './Counter';
import WelcomeButton from './WelcomeButton';
import SyntheticEventDemo from './SyntheticEventDemo';
import CurrencyConverter from './CurrencyConverter';

function App() {
  return (
    <div className="App">
      <header className="App-header">
        <h1>React Event Examples Application</h1>
        <p>Demonstrating various event handling techniques in React</p>
      </header>
      
      <main>
        {/* Counter Component with Multiple Event Handlers */}
        <Counter />
        
        {/* Welcome Button with Function Arguments */}
        <WelcomeButton />
        
        {/* Synthetic Event Demonstration */}
        <SyntheticEventDemo />
        
        {/* Currency Converter Component */}
        <CurrencyConverter />
      </main>
      
      <footer style={{ 
        marginTop: '40px', 
        padding: '20px', 
        backgroundColor: '#f0f0f0', 
        borderRadius: '8px',
        color: '#666'
      }}>
        <p>Â© 2025 Event Examples App - Built with React</p>
        <p>This application demonstrates various React event handling patterns including:</p>
        <ul style={{ textAlign: 'left', maxWidth: '600px', margin: '0 auto' }}>
          <li>Multiple method invocation on button click</li>
          <li>Function calls with arguments</li>
          <li>Synthetic event handling</li>
          <li>Form submission and event handling</li>
          <li>Currency conversion with form events</li>
        </ul>
      </footer>
    </div>
  );
}

export default App;
