import React, { useState } from 'react';

const CurrencyConverter = () => {
  const [rupees, setRupees] = useState('');
  const [euros, setEuros] = useState('');
  const [result, setResult] = useState('');

  // Exchange rate: 1 EUR = 90 INR (approximate)
  const exchangeRate = 90;

  const handleSubmit = (e) => {
    e.preventDefault();
    
    if (rupees && !isNaN(rupees)) {
      const convertedEuros = (parseFloat(rupees) / exchangeRate).toFixed(2);
      setResult(`₹${rupees} = €${convertedEuros}`);
    } else if (euros && !isNaN(euros)) {
      const convertedRupees = (parseFloat(euros) * exchangeRate).toFixed(2);
      setResult(`€${euros} = ₹${convertedRupees}`);
    } else {
      setResult('Please enter a valid amount');
    }
  };

  const handleRupeesChange = (e) => {
    setRupees(e.target.value);
    setEuros(''); // Clear the other input
  };

  const handleEurosChange = (e) => {
    setEuros(e.target.value);
    setRupees(''); // Clear the other input
  };

  const clearAll = () => {
    setRupees('');
    setEuros('');
    setResult('');
  };

  return (
    <div className="component-section">
      <h2>Currency Converter</h2>
      <form className="currency-form" onSubmit={handleSubmit}>
        <div className="form-group">
          <label htmlFor="rupees">Indian Rupees (₹):</label>
          <input
            type="number"
            id="rupees"
            value={rupees}
            onChange={handleRupeesChange}
            placeholder="Enter amount in Rupees"
            step="0.01"
          />
        </div>
        
        <div className="form-group">
          <label htmlFor="euros">Euros (€):</label>
          <input
            type="number"
            id="euros"
            value={euros}
            onChange={handleEurosChange}
            placeholder="Enter amount in Euros"
            step="0.01"
          />
        </div>
        
        <div className="button-group">
          <button type="submit" className="btn">
            Convert
          </button>
          <button type="button" className="btn btn-warning" onClick={clearAll}>
            Clear
          </button>
        </div>
      </form>
      
      {result && (
        <div className="result-display">
          <strong>Conversion Result:</strong> {result}
        </div>
      )}
      
      <div className="message-display">
        <small>Exchange Rate: 1 EUR = ₹90 (approximate)</small>
      </div>
    </div>
  );
};

export default CurrencyConverter;
