import React, { useState } from 'react';

const SyntheticEventDemo = () => {
  const [clickMessage, setClickMessage] = useState('');
  const [eventDetails, setEventDetails] = useState('');

  // Synthetic event handler for OnPress
  const handleOnPress = (e) => {
    // Prevent default behavior
    e.preventDefault();
    
    // Display the click message
    setClickMessage('I was clicked');
    
    // Show event details
    setEventDetails(`
      Event Type: ${e.type}
      Target: ${e.target.tagName}
      Timestamp: ${new Date().toLocaleTimeString()}
      Button: ${e.button === 0 ? 'Left Click' : e.button === 1 ? 'Middle Click' : 'Right Click'}
      Coordinates: (${e.clientX}, ${e.clientY})
    `);
  };

  const handleMouseOver = (e) => {
    setClickMessage('Mouse is hovering over the button!');
    setEventDetails(`Event Type: ${e.type}`);
  };

  const handleMouseOut = (e) => {
    setClickMessage('Mouse left the button area!');
    setEventDetails(`Event Type: ${e.type}`);
  };

  const handleDoubleClick = (e) => {
    setClickMessage('Button was double-clicked!');
    setEventDetails(`
      Event Type: ${e.type}
      Detail: ${e.detail} clicks
      Timestamp: ${new Date().toLocaleTimeString()}
    `);
  };

  const clearMessages = () => {
    setClickMessage('');
    setEventDetails('');
  };

  return (
    <div className="component-section">
      <h2>Synthetic Event Demonstration</h2>
      
      <div className="button-group">
        <button 
          className="btn" 
          onClick={handleOnPress}
          onMouseOver={handleMouseOver}
          onMouseOut={handleMouseOut}
          onDoubleClick={handleDoubleClick}
        >
          OnPress Event Button
        </button>
        <button 
          className="btn btn-info" 
          onClick={handleOnPress}
        >
          Another Click Handler
        </button>
        <button 
          className="btn btn-warning" 
          onClick={clearMessages}
        >
          Clear Messages
        </button>
      </div>
      
      <div className="message-display">
        <strong>Click Message:</strong> {clickMessage}
      </div>
      
      {eventDetails && (
        <div className="result-display">
          <strong>Event Details:</strong>
          <pre style={{ textAlign: 'left', fontSize: '14px', margin: '10px 0' }}>
            {eventDetails}
          </pre>
        </div>
      )}
      
      <div className="message-display">
        <small>
          Try different interactions: single click, double click, hover, and mouse out!
        </small>
      </div>
    </div>
  );
};

export default SyntheticEventDemo;
